var exercise = require('workshopper-exercise')();
var filecheck = require('workshopper-exercise/filecheck');
var execute = require('workshopper-exercise/execute');
var comparestdout = require('workshopper-exercise/comparestdout');
var fs = require('fs');
var path = require('path');
var _  = require('lodash');
var chalk = require('chalk');




exercise = filecheck(exercise);
exercise = execute(exercise);

exercise.addProcessor(function(mode, cb) {
  var filename = path.join(process.cwd(), process.argv[3]);
  var users = [{ 'user': 'barney' }, { 'user': 'fred' }];

  fs.readFile(filename, 'utf-8', function(err, data) {
    try {
      eval(data);
    } catch(e) {
      console.log(chalk.red("Sorry you had a Syntax error! make sure all your quotes are closed\n" + e.message));

      exercise.emit('fail', 'Your clone function is off');
      return cb(void 0, false);
    }

    if(typeof flattenDeep == 'undefined'){
      exercise.emit('fail', 'Make sure you defined flattenDeep properly, remember javascript is particular about spelling and capitilaztion');
      return cb(void 0, false);
    }
    var results = [];

    results.push(_.isEqual(flattenDeep([1, [2, 3, [4]]]),[1,2,3,4]));

    if(mode === 'verify') {
      if(!results.every(function(elem){ return elem;})) {

        exercise.emit('fail', 'Your flattenDeep function is off');
        // false is fail
        cb(void 0, false);
      }else {
        // true is pass
        cb(void 0, true);
      }
    }
  });
});

module.exports = exercise;
