function reject(collection, callback) {
  if(Array.isArray(collection)){
    for(key in collection){
      if(callback(collection[key], key, collection))
        collection.splice(key, 1);
    }
  }

  if(!Array.isArray(collection)){
    for(key in collection){
      if(callback(collection[key], key, collection))
        delete collection[key];
    }
  }

  return collection;
}

