var exercise = require('workshopper-exercise')();
var filecheck = require('workshopper-exercise/filecheck');
var execute = require('workshopper-exercise/execute');
var comparestdout = require('workshopper-exercise/comparestdout');
var fs = require('fs');
var path = require('path');
var _  = require('lodash');
var chalk = require('chalk')




exercise = filecheck(exercise);
exercise = execute(exercise);

exercise.addProcessor(function(mode, cb) {
  var filename = path.join(process.cwd(), process.argv[3]);

  fs.readFile(filename, 'utf-8', function(err, data) {
    try {
      eval(data);
    } catch(e) {
      console.log(chalk.red("Sorry you had a Syntax error! make sure all your quotes are closed"));
      console.log(chalk.yellow(e.message));
      exercise.emit('fail', 'Your syntax is a little off');
      // false is fail
      return cb(void 0, false);
    }

    if(typeof zip == 'undefined'){
      exercise.emit('fail', 'Make sure you defined zip properly, remember javascript is particular about spelling and capitilaztion');
      return cb(void 0, false);
    }
    var results = [];
    var names = ['moe', 'larry', 'curly'], ages = [30, 40, 50], leaders = [true];

    results.push(_.isEqual(zip(names, ages, leaders), [
          ['moe', 30, true],
          ['larry', 40, undefined],
          ['curly', 50, undefined]
    ]));


    if(mode === 'verify') {
      if(!results.every(function(elem){ return elem ;})) {

        exercise.emit('fail', 'Your zip function is off');
        // false is fail
        return cb(void 0, false);
      }
      // true is pass
      return cb(void 0, true);
    }
  });
});

module.exports = exercise;
