function zip() {
  var results = [];
  // get longest argument
  var len = 0;
  for (var ii = 0; ii < arguments.length; ii ++) {
    len = len > arguments[ii].length ? len : arguments[ii].length;
  }
  for (var i = 0; i < len; i ++) {
    var currentResult = [];
    for (var j = 0; j < arguments.length; j ++) {
      currentResult.push(arguments[j][i]);
    }
    results.push(currentResult);
  }
  return results;
}
